<script>
import tools from "../tools.ts";

export default {
  props: {
    label: {
      type: String,
      default: "label",
    },
    value: {
      type: String,
      default: "",
    },
    id: {
      type: String,
      default: "",
    },
    parent_name: {
      type: String,
      default: "",
    },
  },
  methods: {
    get_src: function (value) {
      if (value) return value;
      else
        return "data:image/png;base64,";
    },
    preview_files: async function (event) {
      const val = await tools.readUploadedFileAsImg(event.target.files[0]);

      console.log(val);

      this.$store.commit("id_push_update_" + this.parent_name, {
        id: this.id,
        val: val,
      });

      // console.log(event.path[1].children[2].src=val)

      //this.changed_value = 'aaa'
    },
  },
  watch: {
    value: function (val, oldVal) {
      console.log("vch", val, oldVal, this.id, this.parent_name);
      let data = {};
      data[this.id] = val;
      this.$store.commit("push_update_" + this.parent_name, data);
      //event.path[1].children[2].src=val
    },
  },
};
</script>

<template>
  <label class="avatar input">
    <div class="label">{{ label }}</div>
    <input type="file" accept="image/*" v-on:change="preview_files" />
    <img class="avatar-input" :src="get_src(value)" />
  </label>
</template>

<style scoped lang="scss">
@import "../css/global.scss";

.avatar .avatar-input {
  width: 35px;
  height: 35px;
  color: var(--text-color);
}

.avatar {
  display: block;
}

.avatar-input {
  font-size: 20px;
  font-weight: 400;
  border-radius: var(--border-radius);
  transition: all 0.5s ease;
  background: var(--input-bg-color);
  display: inline-block;
  text-align: center;
  border-color: var(--border-color);
  border-width: var(--border-width);
  border-style: var(--border-style);
}

[type="file"] {
  visibility: hidden;
  width: 0px;
}
</style>
