<script>
import cache from "../cache";
export default {
  data() {
    return {
      user: {},
      menu_visible: false,
      common: this.$store.state["common"],
      themes: [
        { name: "Темная", val: "dark" },
        { name: "Темная объемная", val: "dark_3d" },
        { name: "Светлая", val: "light" },
        { name: "Я блондинка!", val: "pink" },
      ],
      theme: "dark",
      langs: [{ name: "Русский", val: "ru" }],
      lang: { name: "Русский", val: "ru" },
      lock_main_menu: false,
      //instead of user.avatar for new year
      is_new_year: false,
      girl_uuids: 
      [
        '1e943b18-7ba7-4ae1-b185-437bea3491d0',
        'c2c5debe-e9ae-4667-8534-5868dc68d2a7',
        '3bae13d5-a27e-4d03-a953-99a04abde80c',
        '90d2ed30-5a64-4542-a40b-123bb0457e4a',
        '9b8f58c2-45ee-4b2f-8a88-a543d2cd2f9d',
        'ac677ec6-a32b-4c2e-b978-524626b9690a',
        '9a408671-3222-4b49-9f1c-1f1f37ba7b79',
        'e0941f26-8e08-4339-9b13-155ba31c931a',
        '5839f25d-8cd6-491b-88cf-1637f3db7e9d',
        '9e54de53-f3e4-43cc-8989-463475162863',
        '88365a7a-8d1f-4ef7-9b3e-48505bf0465a',
        '50d6fe39-36a6-4044-921d-58218b4e83d1',
        '9965cb94-17dc-46c4-ac1e-823857289e98'
      ],
      bad_santa_girl:
      [
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
      ],
      bad_santa: 'data:image/jpeg;base64,'
    };
  },
  mounted() {
    console.log("Cached profile:", cache.getObject('profile'));
    for (let i = 0; i < this.themes.length; i++) {
      if (cache.getString("theme") === this.themes[i].val) this.theme = this.themes[i];
    }
    this.lock_main_menu = cache.getObject("lock_main_menu");
//document.addEventListener("click", this.close_menu);

    this.update_user()

    cache.set_profile_listener(this.update_user)
  },
  updated() {
    //console.log('uuuuu')
  },

  methods: {
    update_user(){
      try {
        this.user = cache.getObject("profile");
      } catch (err) {}
    },
    logout() {
      cache.setString("user_token", "");
      cache.setObject("profile", {});
      this.$router.push("/login");
    },
    close_menu(e) {
    //  console.log('ccc', e);
    //  if(e.target.localName == "img") return
    //  for (let i in e.path) {
        //if (e.path[i].className == "profile") return;
    //    localName
    //  }
      this.menu_visible = false;
    },
    set_theme(theme) {
      this.theme = theme;
      cache.setString("theme", theme.val)
      let htmlElement = document.documentElement;
      htmlElement.setAttribute("theme", theme.val);
    },
    set_lang(lang) {},
    update_lock_main_menu(value) {
      cache.setObject("lock_main_menu", value)
    },

    get_avatar()
    {
      if(!this.is_new_year) return this.user.avatar ? this.user.avatar : 'https://oboz.myjetbrains.com/hub/api/rest/avatar/7755ec62-dfa1-4c3c-a3a9-ac6748d607c1?dpr=1.25&size=20'
      if(!this.girl_uuids.includes(this.user.uuid)) return this.bad_santa
      const max_santa_count = 10
      let santa_id =Math.floor(Math.random()*max_santa_count)
      if(santa_id > this.bad_santa_girl.length-1) santa_id = this.bad_santa_girl.length-1
      console.log('bad_santa_girl', santa_id)
      return this.bad_santa_girl[santa_id]
    },
    toggle_menu()
    {
      console.log('tm')
      this.menu_visible = !this.menu_visible
    }
    
  },
};
</script>

<template>
  <div class="profile" v-if="common.is_in_workspace" v-click-outside="close_menu">
    <div class="profile-top">
      <img @click="toggle_menu()" :src="get_avatar()"  />
      <div class="profile-username">{{ user.name }}</div>
      <div class="issue-top-button">
        <a
            class="bx bx-plus new-issue-btn"
            title="Создать новую задачу"
            :href="'/issue?t=' + new Date().getTime()"
            tag="i"
        >
        </a>
      </div>


    </div>

    <div v-if="menu_visible" id="profile-menu" class="panel">
      <SelectInput
        label="Тема"
        :values="themes"
        :value="theme"
        :reduce="(obj) => obj.val"
        @update_parent_from_input="set_theme"
        :close_on_select="false"
        :parameters="{ clearable: false }"
      >
      </SelectInput>
      <SelectInput
        label="Язык"
        :values="langs"
        :value="lang"
        :reduce="(obj) => obj.val"
        @update_parent_from_input="set_lang"
        :parameters="{ clearable: false }"
      >
      </SelectInput>
      <BooleanInput
        label="Главное меню по логотипу"
        :value="lock_main_menu"
        @update_parent_from_input="update_lock_main_menu"
        style="margin-bottom: 10px"
      ></BooleanInput>
      <div id="profile-menu-exit" @click="logout()">
        <div>
          <i class="bx bxs-door-open"></i>
          <span>Выход</span>
        </div>
      </div>
    </div>
  </div>
</template>

